export default {
  data: function data() {
    return {
      page: []
    };
  },
  created: function created() {
    this.loaddata();
  },
  methods: {
    loaddata: function loaddata() {
      var _this = this;

      var account = window.sessionStorage.getItem('account');
      this.$http.get('/queryEmpState?account=').then(function (res) {
        console.log(res.data);
        _this.page = res.data;
      });
    },
    formatType: function formatType(row) {
      if (row.type == 0) {
        return '上班打卡';
      } else if (row.type == 1) {
        return '下班打卡';
      } else if (row.type == 2) {
        return '外出打卡';
      } else if (row.type == 3) {
        return '返回打卡';
      }
    },

    /*
    * 0：下班
    * 1：在班
    * 2：外出
    * 3：假期
    * 4：请假
    * */
    formatState: function formatState(row) {
      if (row.state == 0) {
        return '下班';
      } else if (row.state == 1) {
        return '在班';
      } else if (row.state == 2) {
        return '外出';
      } else if (row.state == 3) {
        return '假期';
      } else if (row.state == 4) {
        return '请假';
      }
    }
  }
};
