export default {
  data: function data() {
    return {
      page: [],
      state: ' ',
      isHere: false
    };
  },
  created: function created() {
    this.loaddata();
    this.loadstate();
  },
  methods: {
    loaddata: function loaddata() {
      var _this = this;

      var account = window.sessionStorage.getItem('account');
      this.$http.get('/queryRecordsByActOrType?account=' + account).then(function (res) {
        _this.page = res.data;
      });
    },
    loadstate: function loadstate() {
      var _this2 = this;

      var empId = window.sessionStorage.getItem('empId');
      this.$http.get('/queryEmpStateById/' + empId).then(function (res) {
        if (res.data == 0) {
          _this2.state = '下班';
          _this2.isHere = false;
        } else if (res.data == 1) {
          _this2.state = '在班';
          _this2.isHere = true;
        } else if (res.data == 2) {
          _this2.state = '外出';
          _this2.isHere = false;
        } else if (res.data == 3) {
          _this2.state = '假期';
          _this2.isHere = false;
        } else if (res.data == 4) {
          _this2.state = '请假';
          _this2.isHere = false;
        }
      });
    },
    formatType: function formatType(row) {
      if (row.type == 0) {
        return '上班打卡';
      } else if (row.type == 1) {
        return '下班打卡';
      } else if (row.type == 2) {
        return '外出打卡';
      } else if (row.type == 3) {
        return '返回打卡';
      }
    },
    formatDate: function formatDate(row) {
      var date = new Date(row.time);
      var year = date.getFullYear();
      var month = date.getMonth() + 1;
      var day = date.getDate();
      var hour = date.getHours();
      hour = hour < 10 ? "0" + hour : hour;
      var minute = date.getMinutes();
      minute = minute < 10 ? "0" + minute : minute;
      var second = date.getSeconds();
      second = second < 10 ? "0" + second : second;
      return year + '-' + month + '-' + day + ' ' + hour + ':' + minute + ':' + second;
    },
    changeState: function changeState(num) {
      var _this3 = this;

      var empId = window.sessionStorage.getItem('empId');
      this.$http.post('/checkInOrOut/' + empId + '/' + num).then(function (res) {
        if (res.data) {
          _this3.$message({
            message: '操作成功',
            type: 'success'
          });

          _this3.loaddata();

          _this3.loadstate();

          if (num == 0) {
            _this3.isHere = true;
          } else if (num == 1) {
            _this3.isHere = false;
          } else if (num == 2) {
            _this3.isHere = false;
          } else if (num == 3) {
            _this3.isHere = true;
          }
        } else {
          _this3.$message({
            message: '操作失败',
            type: 'error'
          });
        }
      });
    }
  }
};
