export default {
  data: function data() {
    return {
      page: []
    };
  },
  created: function created() {
    this.loaddata();
  },
  methods: {
    loaddata: function loaddata() {
      var _this = this;

      var empId = window.sessionStorage.getItem('empId');
      this.$http.get('/getLeftTimeById/' + empId).then(function (res) {
        console.log(res.data);
        _this.page = res.data;
      });
    },
    toApply: function toApply(num) {
      this.$router.push({
        path: '/apply/addApply',
        query: {
          type: num
        }
      }); // this.$router.push('/apply/addApply');
    }
  }
};
